    fun setPingTextSignal(context: Context?, imageView: ImageView, latency: Long?) {
        val latencyInt = latency!!.toInt()
        when {
            latencyInt < 200 -> {
                imageView.setImageDrawable(ContextCompat.getDrawable(context!!, R.drawable.full_signals))

            }
            latencyInt < 400 -> {
                imageView.setImageDrawable(ContextCompat.getDrawable(context!!, R.drawable.medium_signals))
            }
            else -> {
                imageView.setImageDrawable(ContextCompat.getDrawable(context!!, R.drawable.no_signals))
            }
        }
    }

    fun getAverageLatencyByCountry(servers: MutableList<Server>): Long {
        val size = servers.size
        var latency = 0L
        for (server in servers) {
            latency += server.getLatency()!!
        }
        return latency / size
    }

    fun sortHashMapByValue(hashMap: HashMap<String, MutableList<Server>>): HashMap<String, MutableList<Server>> {
        val keys = ArrayList<String>()
        keys.addAll(hashMap.keys)
        keys.sortWith { lhs, rhs ->
            val lhsList = hashMap[lhs]
            val rhsList = hashMap[rhs]
            val val1 = getAverageLatencyByCountry(lhsList!!)
            val val2 = getAverageLatencyByCountry(rhsList!!)
            val1.compareTo(val2)
        }

        val newHashMap = LinkedHashMap<String, MutableList<Server>>()
        for (key in keys) {
            Logger.d("Aman", "-----------------> $key")
            val servers = hashMap[key]
            for (server in servers!!) {
                Logger.d("Aman", "-----------------> $server")
            }
            newHashMap[key] = servers
            Logger.e("Aman", "----------------------")
        }
        return newHashMap
    }
